import React, { useState } from "react";

function ToDoItem(props){

const [isLineThrough,setLineThrough]=useState(true);


function handleClick(event)
{      

        setLineThrough(false);

    
}



return (
 <div>
       <li id="abc" style={{textDecoration:isLineThrough?"":"line-through"}} onClick={handleClick}>{props.text}</li>
 </div>
)


}

export default ToDoItem;
