import React, { useState } from "react";

function ToDoItem(props){

const [isLineThrough,setLineThrough]=useState(false);


function handleClick(event)
{      
    //setLineThrough(true);
    document.getElementById("abc").style.textDecoration="line-through";
    document.getElementById("abc").style.textDecoration="none";
    
    

    
}



return (
 <div>
       <li id="abc" onClick={handleClick}>{props.text}</li>
 </div>
)


}

export default ToDoItem;
