import React, { useState } from "react";

function ToDoItem(props){

const [isLineThrough,setLineThrough]=useState(false);


function handleClick(event)
{      

        if(isLineThrough===false)
        {
            isLineThrough(true);
        }
        else if(isLineThrough===true)
        {
            isLineThrough(false);
        }

    
}



return (
 <div>
       <li id="abc" style={{textDecoration:isLineThrough?"":"line-through"}} onClick={handleClick}>{props.text}</li>
 </div>
)


}

export default ToDoItem;
